r -->\n\n<!-- wp:spacer {\"height\":70} -->\n<div style=\"height:70px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:spacer {\"height\":70} -->\n<div style=\"height:70px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"className\":\"animate-fade-up\",\"editorskit\":{\"devices\":false,\"desktop\":true,\"tablet\":true,\"mobile\":true,\"loggedin\":true,\"loggedout\":true,\"acf_visibility\":\"\",\"acf_field\":\"\",\"acf_condition\":\"\",\"acf_value\":\"\",\"migrated\":false,\"unit_test\":false}} -->\n<h2 class=\"has-text-align-center animate-fade-up\">Best seller you can’t miss</h2>\n<!-- /wp:heading -->\n\n<!-- wp:spacer {\"height\":50} -->\n<div style=\"height:50px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:yith/yith-wc-productslider {\"className\":\"animate-fade-up\"} -->\n<div class=\"wp-block-yith-yith-wc-productslider animate-fade-up\"><span class=\"yith_block_e6b986bd6c1f8bfa94544d41af558d7a\">[yith_wc_productslider id=\"268\"]</span></div>\n<!-- /wp:yith/yith-wc-productslider -->\n\n<!-- wp:spacer {\"height\":50} -->\n<div style=\"height:50px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:group {\"align\":\"full\",\"style\":{\"color\":{\"background\":\"#faf1e0\"}}} -->\n<div class=\"wp-block-group alignfull has-background\" style=\"background-color:#faf1e0\"><div class=\"wp-block-group__inner-container\"><!-- wp:media-text {\"align\":\"\",\"mediaPosition\":\"right\",\"mediaId\":229,\"mediaType\":\"image\",\"mediaWidth\":53,\"className\":\"animate-fade\",\"editorskit\":{\"devices\":false,\"desktop\":true,\"tablet\":true,\"mobile\":true,\"loggedin\":true,\"loggedout\":true,\"acf_visibility\":\"\",\"acf_field\":\"\",\"acf_condition\":\"\",\"acf_value\":\"\",\"migrated\":false,\"unit_test\":false}} -->\n<div class